create table users (
  id int unsigned primary key auto_increment not null,
  email varchar(255) not null unique,
  password varchar(64) not null,
  hashed_password varchar(255) not null
);

create table clients (
  id int unsigned primary key auto_increment not null,
  users_id int unsigned not null,
  foreign key(users_id) references users(id)
  ON DELETE CASCADE
);

create table admins (
  id int unsigned primary key auto_increment not null,
  users_id int unsigned not null,
  foreign key(users_id) references users(id)
  ON DELETE CASCADE
);

create table books (
  id int unsigned primary key auto_increment not null,
  title varchar(255) not null,
  resume text not null,
  illustration LONGTEXT not null
);

create table books_clients (
  id int unsigned primary key auto_increment not null,
  date timestamp,
  price decimal not null,
  books_id int unsigned not null,
  foreign key(books_id) references books(id),
  clients_id int unsigned not null,
  foreign key(clients_id) references clients(id)
);

create table episodes (
  id int unsigned primary key auto_increment not null,
  title varchar(255) not null,
  to_register boolean default false,
  type varchar(255) not null,
  books_id int unsigned not null,
  is_free boolean default false,
  foreign key(books_id) references books(id)
  ON DELETE CASCADE
);

create table choices (
  id int unsigned primary key auto_increment not null,
  text text not null,
  episodes_source_id int unsigned not null,
  foreign key(episodes_source_id) references episodes(id),
  episodes_target_id int unsigned not null,
  foreign key(episodes_target_id) references episodes(id)
  ON DELETE CASCADE
); 

create table illustrations (
  id int unsigned primary key auto_increment not null,
  url LONGTEXT not null,
  episodes_id int unsigned not null,
  foreign key(episodes_id) references episodes(id)
  ON DELETE CASCADE
);

create table paragraphs (
  id int unsigned primary key auto_increment not null,
  content text not null,
  episodes_id int unsigned not null,
  foreign key(episodes_id) references episodes(id)
  ON DELETE CASCADE
);

create table progression (
  date timestamp,
  episodes_id int unsigned not null,
  foreign key(episodes_id) references episodes(id),
  clients_id int unsigned not null,
  foreign key(clients_id) references clients(id)
);

CREATE TABLE glossary (
  id INT UNSIGNED PRIMARY KEY AUTO_INCREMENT NOT NULL,
  word VARCHAR(255) NOT NULL,
  definition TEXT NOT NULL
);

INSERT INTO `books` VALUES (1,'Perdu dans ZERA','Es-tu prêt pour entrer dans Zera ?  \n\nZera ? Un monde virtuel imaginé par l’auteur. Tu vas littéralement être propulsé dans cette autre réalité. \n\nRelève le défi et incarne ton propre personnage à l’intérieur du métavers. Tu auras la possibilité de vivre des aventures dans différentes maps et différentes époques.\n\nQue ce soit seul ou accompagné de ton petit frère, dans l’univers du Far West ou dans la Cybercité, ta mission sera de réussir à quitter à quitter le jeu sain et sauf !\n\nPour cela,tu vas rencontrer de nombreux personnages, mener des  quêtes plus insolites les unes que les autres et tu ne pourras t’en remettre qu’à ton instinct et à un peu de hasard !  Perdu dans Zera est un livre intéractif dont tu es le héros, à chaque fin d’épisode tu pourras choisir la suite du récit. Alors essaye de faire le bon choix …\n\nRichement illustré par l’auteur, le livre est également accompagné d’un glossaire pour te familiariser avec le vocabulaire du Web 3 et de l’informatique. \n','data:image/webp;base64,');
